-- Tablo döküm verisi `movie_news`
--

INSERT INTO `movie_news` (`id`, `entry_date`, `movie_name`, `news_title`, `card_image`, `card_content`, `content`) VALUES
(6, '2024-01-06 21:05:51', 'Love Actually', '‘Love Actually’ 20. Yıl Dönümü İçin Sinemalara Geri Dönüyor!', '../images/news/hugh-grant-love-actually.webp', 'Kült romantik komedi ‘Love Actually’, 20. yıldönümünü kutlamak için restore edilmiş 4K versiyonuyla sinemalarda olacak.\nRichard Curtis‘in 2003 tarihli sevilen başyapıtı Love Actually, gösterime girmesinden yirmi Noel sonra bile romantik komediler arasında zamansız bir klasik olmayı sürdürüyor. Studiocanal ve Universal Pictures, 20. yıldönümünü kutlamak için güçlerini birleştirerek dünyanın dört bir yanındaki hayranlara filmin büyüsünü bir kez daha, bu kez çarpıcı 4K netliğinde deneyimleme fırsat', '&lt;h4 class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-center cf-tt-target-attached-center cf-tt-element-attached-top cf-tt-target-attached-bottom cf-tt-enabled cf-tt-out-of-bounds-top&quot; style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;Kült romantik komedi ‘Love Actually’, 20. yıldönümünü kutlamak için restore edilmiş 4K versiyonuyla sinemalarda olacak.&lt;/h4&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Richard Curtis&lt;/span&gt;‘in&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2003&lt;/span&gt;&amp;nbsp;tarihli sevilen başyapıtı&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Love Actually,&lt;/em&gt;&amp;nbsp;gösterime girmesinden yirmi Noel sonra bile romantik komediler arasında zamansız bir klasik olmayı sürdürüyor. Studiocanal ve Universal Pictures, 20. yıldönümünü kutlamak için güçlerini birleştirerek dünyanın dört bir yanındaki hayranlara filmin büyüsünü bir kez daha, bu kez çarpıcı&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;4K&lt;/span&gt;&amp;nbsp;netliğinde deneyimleme fırsatı sunuyor. Bu restore edilmiş 4K versiyonu hem evde izlenebilecek hem de dünya çapındaki sinemalarda da gösterilecek.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/film__10619-love-actually-hi_res-93c067f3.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;aligncenter size-full wp-image-144247&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/film__10619-love-actually-hi_res-93c067f3.jpg&quot; alt=&quot;&quot; width=&quot;970&quot; height=&quot;546&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/film__10619-love-actually-hi_res-93c067f3.jpg 970w, https://filmhafizasi.com/wp-content/uploads/2023/12/film__10619-love-actually-hi_res-93c067f3-300x169.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/12/film__10619-love-actually-hi_res-93c067f3-768x432.jpg 768w&quot; sizes=&quot;(max-width: 970px) 100vw, 970px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Hollywood’un en büyük yıldızlarından oluşan ekibin canlandırdığı rüya gibi çiftlerle dolu&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Love Actually&lt;/em&gt;, romantik komedilerin en iyilerinden biri. Pek çok nedenden ötürü benzerlerinin önüne geçiyor ve bu nedenle çoğu kişinin mutlaka izlenmesi gereken tatil filmleri listesinde yer alıyor. Londra’da Noel zamanı geçen filmde,&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Hugh Grant, Thampson, Chiwetel Ejiofor, Alan Rickman, Keira Knightley, Colin Firth, Bill Nighy&amp;nbsp;&lt;/span&gt;ve&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Liam Neeson&lt;/span&gt;‘ın da aralarında bulunduğu A sınıfı oyuncu kadrosunun olağanüstü performanslarına sahne oldu.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Love Actually&lt;/em&gt;‘nin&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;4K&lt;/span&gt;&amp;nbsp;restorasyonu&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Universal Pictures&lt;/span&gt;‘ın 35mm orijinal negatifinden elde edildi ve nihai ürün Londra ve Los Angeles’taki stüdyoların ortak çalışmasıyla ortaya çıktı. İngiliz renk uzmanı&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Adam Glassman&lt;/span&gt;, yönetmen&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Curtis&lt;/span&gt;‘in gözetiminde filmin renk tonlamasını gerçekleştirdi ve filmin Dolby Vision’daki ilk HDR versiyonunu üretti.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/231204123625-07-love-actually-cringiest-moments.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;aligncenter size-full wp-image-144246&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/231204123625-07-love-actually-cringiest-moments.jpg&quot; alt=&quot;&quot; width=&quot;1110&quot; height=&quot;757&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/231204123625-07-love-actually-cringiest-moments.jpg 1110w, https://filmhafizasi.com/wp-content/uploads/2023/12/231204123625-07-love-actually-cringiest-moments-300x205.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/12/231204123625-07-love-actually-cringiest-moments-1024x698.jpg 1024w, https://filmhafizasi.com/wp-content/uploads/2023/12/231204123625-07-love-actually-cringiest-moments-768x524.jpg 768w&quot; sizes=&quot;(max-width: 1110px) 100vw, 1110px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Dijital platformlar için 4K olarak yeniden yayınlanacak olan filmde, Londra’da çekilen ve filmin oyuncu kadrosu ile yapımcılarının filmle ilgili görüşlerini ve filmin yapım aşamasındaki anılarını paylaştıkları 30 dakikalık yeni bir belgesel de yer alacak. Yönetmen&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Curtis&lt;/span&gt;,&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Filmin bu yeni versiyonu için gerçekten heyecanlıyım; hiç bu kadar iyi görünmemiş ve duyulmamıştı,” diyor. “Ne kadar yaşlandığıma biraz şaşırmış olsam da, filmi yapan herkesin filmle ilgili yeni düşünceleri ve anılarıyla dolu olan belgesele bayıldım.”&lt;/em&gt;&amp;nbsp;diyor.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Yeni 4K restorasyon Türkiye’de&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;29 Aralık 2023&lt;/span&gt;&amp;nbsp;tarihinde vizyona girecek.&lt;/p&gt;');
INSERT INTO `movie_news` (`id`, `entry_date`, `movie_name`, `news_title`, `card_image`, `card_content`, `content`) VALUES
(7, '2024-01-06 21:08:14', 'yok', '2023 Yılının En Çok İzlenen Netflix Yapımları!', '../images/news/netflix.webp', 'Netflix, 2021’de haftalık En İyi 10 ve En Popüler listelerimizi kullanıma sunduğundan bu yana, insanların ne izlediği hakkında YouTube dışındaki diğer yayıncılardan daha fazla bilgi sağladı. Ne İzledik: Netflix Etkileşim Raporu’nu yılda iki kez yayınlayan platform sahipleri, bu şekilde, altı aylık bir süre boyunca insanların Netflix’te neler izlediğini gösteren kapsamlı bir rapor sunmuş oluyorlar.\n\n2023’ün İlk 6 Ayında En Çok İzlenen Diziler\nThe Night Agent ilk üç ayında 812 milyon saat izlenme ', '&lt;h4 style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;&lt;span class=&quot;cf-tweet-this cf-tt-target cf-tt-enabled cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-out-of-bounds-top cf-tt-element-attached-top cf-tt-element-attached-center cf-tt-target-attached-bottom cf-tt-target-attached-center&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Netflix,&lt;/span&gt;&amp;nbsp;geçtiğimiz haftalarda yayınladığı raporda&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2023 yılının ilk altı ayındaki&lt;/span&gt;&amp;nbsp;izlenme sayılarını paylaştı. 18 binden fazla içeriğin izlenme süresinin paylaşıldığı raporda toplam izlenme süresi yaklaşık&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;100 milyar saat&lt;/span&gt;&amp;nbsp;olarak kaydedildi. Yapılan incelemeler sonucu en çok izlenen film ve diziler şöyle:&lt;/h4&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/Netflix-ne-kadar-internet-yer-rehber-kapak.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;aligncenter size-full wp-image-144207&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/12/Netflix-ne-kadar-internet-yer-rehber-kapak.jpg&quot; alt=&quot;&quot; width=&quot;1177&quot; height=&quot;603&quot; srcset=&qu