--
-- Tablo için tablo yapısı `movie_reviews`
--

CREATE TABLE `movie_reviews` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `entry_date` timestamp NOT NULL DEFAULT current_timestamp(),
  `movie_name` varchar(100) NOT NULL,
  `review_title` varchar(100) NOT NULL,
  `card_image` text NOT NULL,
  `card_content` varchar(500) NOT NULL,
  `content` longtext NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Tablo döküm verisi `movie_reviews`
--

INSERT INTO `movie_reviews` (`id`, `entry_date`, `movie_name`, `review_title`, `card_image`, `card_content`, `content`) VALUES
(77, '2024-01-06 20:04:57', '2001: A Space Odyssey', 'Bilinç Dışından Bilince Bir Uzay Macerası: 2001', '../images/reviews/2001-a-space-odyssey.jpg', 'Başlangıçta hiçlik ebediyken belirsiz bir nedenle patlar ve varlık oluşur. Günümüzde halen gizemini koruyan bu patlamadan milyarlarca yıl sonra Kubrick, henüz başlangıç jeneriği girmeden ani bir açılışla maddenin olmadığı, tahayyül bile edemeyeceğimiz o karanlığı görüntüler yerine György Ligeti’nin gizemli Atmospheres uvertürü ile tasvir eder. ', '&lt;p class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-center cf-tt-target-attached-center cf-tt-enabled cf-tt-out-of-bounds-top cf-tt-element-attached-top cf-tt-target-attached-bottom&quot; style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Başlangıçta hiçlik ebediyken belirsiz bir nedenle patlar ve varlık oluşur. Günümüzde halen gizemini koruyan bu patlamadan milyarlarca yıl sonra Kubrick, henüz başlangıç jeneriği girmeden ani bir açılışla maddenin olmadığı, tahayyül bile edemeyeceğimiz o karanlığı görüntüler yerine György Ligeti’nin gizemli&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Atmospheres&lt;/em&gt;&amp;nbsp;uvertürü ile tasvir eder.&amp;nbsp;&lt;em class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-center cf-tt-target-attached-center cf-tt-element-attached-bottom cf-tt-target-attached-top&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2001: A Space Odyssey&lt;/em&gt;‘in hemen başında karşılaştığımız bu maddesiz, dolayısıyla görüntüsüz hiçliğin 300.000 yıllık patlamasını Kubrick, birçok seyirciye uzun gelen bir sürede, üç-beş dakikada anlatmayı dener. Bu eşsiz&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Bigbang&lt;/em&gt;&amp;nbsp;tasavvurundan sonra film başladığında ilk tarihsel sıçrama gerçekleşir ve dünyanın soğumuş yalnız haliyle baş başa kalırız.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Kubrick’in seyirciyi dakikalarca yeryüzü şekillerine odaklaması, daha önce varlığından söz edemeyeceğimiz bu şekillere ilk defa görüyormuş gibi bakmamızı istemesinden kaynaklanır. Çünkü ancak o zaman&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;hiç&lt;/em&gt;&amp;nbsp;ile&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;var&lt;/em&gt;‘ın farkı anlaşılabilecektir. İkinci sıçrama gerçekleştiğinde -ki bu uzun tarihsel sıçramalar sinema tarihinde bir ilktir- Kubrick bizleri günümüzden 4.000.000 yıl önceye götürür ve objektiflerini&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Australopithecus&lt;/em&gt;‘lara (iki ayaküstünde durabilen insansılar) çevirir. Evrim tarihine baktığımızda göreceğimiz üzere Australopithecus’ların daha sonra yolları ayrılacaktır ve ayrışma sonucunda modern insanın ilk uzak atası&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Homo habilis&lt;/em&gt;(yetenekli insan) ortaya çıkacaktır. Kubrick, Homo habilis’in ortaya çıkışını&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;siyah yekpare dikilitaş&lt;/em&gt;a dokunan ve daha sonra&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Homo sapiens&lt;/em&gt;&amp;nbsp;olarak evrim basamaklarını tırmanacak kabile ile anlatır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: center;&quot;&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;1. İnsanlığın doğuşu&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/2001-monolith.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;br&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;4.000.000 yıl önce bir gece kabilenin yanı başına dünya dışından kusursuz geometride bir taş iner. Kubrick, bu yekpare dikilitaşı halen evrenin en büyük gizlerinden biri olan&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;büyük sıçrama’&lt;/em&gt;yı yani&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;beynin evrimi’&lt;/em&gt;ni, “bilinç”in kaynağını simgelemek için kullanır. Dünyanın rastlantısal üretimlerine benzemeyen, eşi benzeri görülmemiş bu nesne karşısında dehşete kapılan Australopithecuslar merakları sayesinde korkularını yenip taşa dokunurlar ve “yaratıcı sembolik düşünce” açığa çıkar.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Kubrick, açıkça siyah dikilitaş vasıtasıyla bilincin dünya dışından insansılara bahşedildiğini ima eder. Evrim tarihinde ilk defa bir canlı türü bulunduğu ortam koşullarına uygun bir organizmaya evrilmek yerine yaratıcı düşünceyi geliştirir ve “alet” yapmak suretiyle çevresini değiştirmeye başlar. Bunun sebebine, gelinen koşullarda yok olma tehlikesinde bulunan bir türün ölüme çare araması olarak da bakabiliriz. Kubrick bunu hayatta kalmaya çalışan iki maymun-insan kabilesinin savaşıyla anlatır. Kısıtlı su kaynakları yüzünden savaşan bu kabilelerden biri su kaynaklarını ele geçirerek diğer kabileyi ölüme terk eder. Bu durumun ertesinde&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;dikilitaş’&lt;/em&gt;a dokunan kabile reisi ilk aleti keşfedecektir. Eline aldığı sert bir kemik parçasıyla yıkım gücünün farkına varan kabile reisi daha önce etraflarında olan fakat öldüremedikleri hayvanları bir bir öldürmeye başlar. Dolayısıyla yaratıcı sembolik düşüncenin gelişimi kıtlığa çare olur.&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/2001-opening.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: center;&quot;&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Bulundukları koşulda kendi bedenlerinde var olan hiçbir uzuvla yemek bulamayan bir canlı türü, kendine protez yapmak suretiyle eksikliğini giderip tekrar avcı konumuna gelir. Artık su kaynaklarını geri almanın zamanı gelmiştir. İki kabile karşı karşıya gelir ve alet yapmayı öğrenen kabile ilk cinayeti işleyip bölgesine tekrar hakim olur. Bu “bilinç”in hayvan üstündeki zaferidir, fakat hayvan dürtüsüyle. Kubrick bu zaferi sinema tarihinin en meşhur geçişiyle ilk alet olan kemiğin havaya atılmasıyla görselleştirir ve filmdeki son büyük tarihsel sıçrama gerçekleşir. Silah olarak kullanılan kemikten uzay gemisine aletlerin geçirdiği değişim böylelikle anlatılmış olur. Artık Kubrick’in elinde arkaplanı sağlam bir hikaye vardır, “alet”in nasıl hayvani tarafımızdan geldiğini ve neden kötü olduğunu anlatabilecektir. Çünkü bilinç, hayvan güdüsünün hizmetinde kullanılmıştır ve ilk atalarımızdan bize kalan, rekabet için alet üretme geleneği olmuştur.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2. TMA-1&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Kubrick bu bölümde rekabet bilincinin ihtişamını ve -filmin çekildiği tarih de göz önüne alınırsa- kendi filminin ihtişamını gözler önüne serer. Sinema tarihinde o zamana dek görülmemiş uzay sahneleri ve görüntülü telefon gibi icatlara sebebiyet veren aletler mevcuttur. Henüz insanlık Ay’a ayak basmamışken dünyanın uzaydan nasıl göründüğünü hayal bile edemezken Kubrick seyircilere&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2001&lt;/em&gt;&amp;nbsp;ile dünyayı göstermeye çalışır. Gerçekten de o dönemde Kubrick, Arthur C. Clarke gibi usta bir bilim kurgu yazarını da yanına alarak daha önce eşi benzeri olmayan bir bilim-kurgu filmi çekmiştir. Bundan sonra bilim-kurgu filmlerinde gözler görülür bir kalite artışı olur ve türün seyircileri için&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2001: A Space Odyssey&lt;/em&gt;, bilim-kurgu türünde bir mihenk taşı olur.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Günümüz seyircilerinin 2001’den en çok şikayetçi olduğu “uzun plan uzay sahneleri”, aslında gerçekten filmin anlatımına çok da katkı sağlamazlar. Yine de Kubrick’in bu sahnelerde anlatmak istediği bazı düşünceler vardır. Bu sahnelerin uzun olmasında elbette teknik olarak çok emek verilmiş bir filmin ihtişamını gösterme isteği fazlasıyla kendini gösterir. Ancak filmin çekildiği “Soğuk Savaş” dönemi göz önüne alınırsa Kubrick’in dünya devletlerinin içinde bulunduğu rekabeti anlatmak için neden uzayı ve görkemli teknolojik aletleri seçtiği anlaşılabilir. İlk bölümde bahsettiğim üzere insanlık, “bilinç”i gösteriş ve iktidar ilişkileri için kullanmaya başlamıştı ve Kubrick’in filmi çektiği döneme baktığımızda artık “bilinç”in&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Uzay Yarışı&lt;/em&gt;‘nda kullanıldığını görürüz.&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/2001-tma1.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: center;&quot;&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Kubrick, Soğuk Savaş döneminde SSCB ve ABD arasında baş gösteren uzay rekabetinin daha uzun süreceğini öngörmüş fakat yanılmıştır. Bu dönemde teknolojik olarak ABD’nin üstünlüğü dünya tarafından onaylanmışken SSCB’nin uzay teknolojilerinde ilklere imza atması ABD’yi yarışta geriye iter ve bu durum Amerikan halkını dehşete düşürür. Bu korku başkanlık seçimlerinde en büyük koz olarak kullanılır ve Kennedy, Ay’a ilk ayak basacak insanın bir Amerikalı olacağına dair verdiği söz üzerine başkan seçilir. Ay’a gitme projesi milli bir sorun haline gelir ve bütün halk ellerinde maket roketleriyle çocuklar dahi gelişmeleri yakından takip eder hale gelir.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Apollo&lt;/em&gt;&amp;nbsp;adı verilen proje bütün ülke ekonomisi hiçe sayılarak birçok başarısız denemeyle birlikte 6 yıl gibi kısa bir sürede, “acele” ile gerçekleştirilir ve Ay’a ilk ayak basan bir Amerikalı olur. Bütün dünyanın gözleri önünde ABD, Ay’ın yüzeyine diktiği bayrakla zaferini ilan etmiştir. Buna karşılık olarak SSCB’nin girişimleri olsa da ekonomik sebeplerden dolayı yarıştan çekilir ve Ay’a gitmenin gereksiz olduğunu vurgulayarak buraya ayrılacak bütçenin kendi halkının refahı için kullanılacağını duyurur. Daha sonra SSCB’nin dağılmasıyla uzay yarışı tamamen sona erer ve aslında bu yarışın gereksiz olduğu anlaşılır. Nitekim Kennedy, NASA müdürü Webb ile yaptığı bir sohbet sırasında bunu da şu sözlerle itiraf eder, “Yaptığımız her şey Ay yolunda Rusları geçmek için. Yoksa bu kadar parayı harcamamamız gerekir, çünkü ben uzayla ilgilenmiyorum. Bu bedeli karşılayacak tek şey Sovyetleri yenip, geride kaldığımız birkaç yılı sonlandırmak.”&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Görüldüğü üzere, ortada insanlık adına yapılan bir girişimden ziyade dünya devletlerinin birbirlerine üstünlüklerini kabul ettirme çabası kendini gösterir. Dolayısıyla Kubrick, tüm bu aletlerin ihtişamını gösterirken aslında insan evladının en ilkel rekabet güdüsünün bunlara sebep olduğunu anlatmak ister. İlk alet kemik diğer kabileye üstünlük kurmak için kullanılmıştı, 2001’e geldiğimizde ise alet özünden hiçbir şey kaybetmemiş ve yine üstünlük kurmak için uzay yolunda kullanılmıştır. Kemiğin gökyüzünde süzülüşünden kalemin yerçekimsiz ortamda süzülüşüne geçtiğimizde&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;yazının icadı&lt;/em&gt;na dahi bir gönderme yakalayabiliriz. Bilindiği üzere yazının icadı iktidarın resmiyete dökülmesi amacıyla kullanılmış, çoğunlukla siyasal ve ekonomik ilişkileri, kölelik ve mülkiyet haklarını belirlemek ve düzenlemek için kullanılmıştır. Demem o ki, Kubrick bariz bir sistem eleştirisi yapmaktadır. Aksi halde o kaleminin uzun uzun yerçekimsiz ortamda süzülüşünü gösteren sahneyi gereksiz addetmemiz gerekecektir. Kubrick’in dehası ortadayken sanırım yaptığı bu sistem eleştirisini görmezden gelmek haksızlık olacaktır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tarihsel arka planı anlattıktan sonra filme dönersek, Ay’da 4.000.000 yıl önce gömüldüğü tespit edilen ikinci yekpare dikilitaş bulunur. ABD yetkilileri bunu tüm dünyadan özellikle de SSCB’den saklamaktadır. Çünkü bu taşın insanlığın en büyük buluşu olduğu düşünülmektedir. Ve her zaman olduğu gibi bu tarz buluşların öncelikle iktidar tarafından işlerine yarayıp yaramadığı test edilmelidir. Bunun üzerine taşı incelemek için bir ekip yola koyulur. İlk bölümde olduğu gibi taş bütün ihtişamıyla insanların karşısındadır. Bu sefer gördüğü karşısında dehşete kapılmış bir türden bahsetmek mümkün değildir. Artık taşın karşısında, önüne ne çıkarsa bunu iktidar ilişkilerine göre yontan bir tür vardır. Ve ekip bu eşi benzeri olmayan buluşun önünde video çektirmek için dizildiklerinde kulakları sağır eden bir sinyal yayılır. Tarih tekerrür eder ve insan evladı yine “bilinç”i kendi çıkarı için hayvani güdülerinin tatmini için kullanmaya yeltenir. Kubrick’in bu video çekme sahnesinde sinema dünyasına dahi güç-iktidar ilişkileri açısından bir eleştiri getirdiği söylenebilir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;3. Jüpiter Görevi&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Ay’da tespit edilen ikinci dikilitaşın işaret ettiği yere, on sekiz ay sonra Jüpiter’e yolculuk başlar. Bu yolculuk sırasında aletlerin en kusursuzu&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;HAL 9000&amp;nbsp;&lt;/em&gt;ile tanışırız. Kubrick HAL’in gözünden insanları değerlendirmeye başlar. Satranç sahnelerinde insanın kendi yarattığı alet karşısındaki acizliği vurgulanır. Fakat HAL resim çizebilen Dave’e karşı inceden bir kin beslemektedir. HAL’ın henüz olaylar cereyan etmeden önce Dave’e olan takıntısını, insandan zeka olarak her türlü daha üstün olduğunu bilen bir makinanın sanat karşısındaki çaresizliği ile açıklanabileceğini düşünüyorum. HAL her ne kadar insanlıktan üstün bir “alet”i temsil etse de duygudan yoksunluk ya da ruhu anlama yetisinin olmayışı “sanat” dediğimiz insana özgü bu yaratıcı davranışı taklit etmesini imkansız kılmaktadır. Kubrick burada Dave’in çizimlerini HAL’a gösterirken, insanın bütün zayıflıklarına rağmen sanat vasıtasıyla nasıl “alet”den farklı olduğunu vurgular.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: center;&quot;&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Kusursuzluğu ile nam salmış HAL serisi bilgisayar görev sırasında bir hata yapar. Bu hata sonucunda güvenirliliği sorgulanmaya başlayan kusursuz alet HAL, iyiden iyiye insanlara/insanlığa cephe almaya başlamıştır. Mürettebat HAL’ı kapatmak için karar aldığında filmin gerilimi artar ve HAL kendi hatasını telafi etmek için geminin dışında bulunan görevliyi uzayın derinliklerinde ölüme terk eder. Bu kısımlarda artık diyebiliriz ki, “aletin, insana karşı savaşı” başlamıştır. Kubrick’in elbette işaret ettiği, özünde en ilkel rekabet güdüsünden kaynaklı alet üretiminin sonunda insanla rekabet eden bir alet’in üretilmesiyle sonuçlanabileceğidir.&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/2001-hal-900.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Arkadaşını kurtarmak için uzaya açılan Dave artık HAL’ın tamamen güvenilmez olduğunu anlar. Uzayın boşluğunda salınan arkadaşını gemiye sokup, yine de sorunun neden kaynaklandığını anlamak isteyen Dave, onu gemiye almayı reddeden HAL ile karşı karşıya kalır. Bunun sonucunda HAL’ın tamamen gemiyi insanlardan arındırıp kendi başına Jüpiter’e gitmeyi planladığını anlarız. Jüpiter’e kim giderse artık zafer onun olacaktır. Her şey HAL’ın planladığı gibi gitmesine rağmen Kubrick bizlere filmin en başında olduğu gibi insanın tahmin edilemez inatçı yapısını gösterecektir. Nasıl ki atalarımız mevcut koşullar ile uyum içerisinde olmadığı halde tarih öncesinde ölüme direndiyse şimdi de Dave ölüme direnmektedir. İnsanın inatçılığını ve azmini hesaba katamayan HAL, Dave’in bütün ölümcül tehlikelere rağmen gemiye girmeyi başarmasıyla korkmaya başlar. Dave, HAL’ı kapatmak için geminin kontrol odasına giderken, HAL insanın en çok yaptığı şey olan “yalan”ı devreye sokar ve Dave’i ikna etmeye çalışır. Sonunda HAL’ın bütün yalvarmalarına karşın Dave insanlık tarihinin başına dönerek en ilkel silah olan kemiğin başkalaşım geçirmiş dengi, tornavida ile HAL’ın hayatına son verir. Böylelikle insan tekrar en ilkel haline dönüp aletlerin en basitiyle yarattığı en gelişmiş “alet”i yok ederek kendini özgürleştirir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;4. Jüpiter ve Sonsuzluğun Ötesi&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Biraz da Kubrick’in “Jüpiter” seçiminin üzerinde durmak gerekirse, Jüpiter, Güneş sistemimizin en büyük gezegeni olmasının yanında bilindiği üzere tanrılar tanrısı Zeus’u simgelemektedir. Aynı zamanda astrolojide Jüpiter, değişim ve gücün kaynağı olarak görülür. Yani güneş sistemimizde bir mutlak güç simgelemek isteseydik bunu ancak Jüpiter ile yapabilirdik. Böylelikle insan sonsuzluğun ötesine, Jüpiter’in içinde bulduğu evrenin bilincini temsil eden yekpare taş ile geçebilecektir. Evrenin bilgisine vakıf olmaya, aydınlanmaya başlayan insanlığın bu geçişini bir renk cümbüşüyle tasvir eden Kubrick, geçiş tamamlandıktan sonra bütün fütürizmini bir kenara bırakıp sürrealist bir ressam gibi hazırladığı beyaz odasına bizleri davet eder.&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/2001-jupyter-mission.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: center;&quot;&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;İnsanlığı temsilen kendini&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;sonsuzluğun ötesinde&lt;/em&gt;&amp;nbsp;bulan Dave, karşısında kendi yaşlanmış halini bulur. Usta bir geçişle yaşlı Dave arkasını dönerken astronot Dave -yani insanlığın rekabet tarihi- kaybolur. Kubrick açıkça insanlığın son yemeğini tasvir ederken bizleri de sona hazırlar. Yaşlı insanlık, onun temel yaşam kaynağı olan, vücudunun üçte ikisini kaplayan suyu, tarih boyunca yemek masasından hiçbir zaman eksik etmemiştir. Bu insanın son yemeği için de geçerlidir ve Kubrick’in yemek masası tasvirinde kristal bir bardağın içinde su vardır. İnsan doğasının sonucunda bu kadeh kırılır ve su zemine dağılır. Bulunduğu kabın şeklini alan su, kabı kırılsa dahi varlığını sürdürecektir. Bu eşsiz bilinç ve beden tasvirinden sonra son yemeğini yiyen insanlık kendini ölüm döşeğinde bulur. Artık beden kırılmış, çürümüş ve bilinç kalmıştır. Hayvanı geride bırakması gerektiğini anlayan insan ellerini son kez, bu sefer anlamış olmanın verdiği o dayanılmaz istekle “dikilitaş”a, mutlak “bilinç”e uzatır. Buradan Michelangelo’nun Sistine Şapel’in tavanındaki ünlü “Âdem’in Yaratılışı” freskine de bir gönderme yakalayabiliriz. Bunu bir de Strauss’un Nietzsche’nin başyapıtı&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Böyle Buyurdu Zerdüşt’&lt;/em&gt;ten ilhamla yazdığı senfonik şiirle birleştiren Kubrick, karşımıza yıldız-çocuğu çıkarır. Artık bilinç özgürleşmiş, hayvan ölmüş ve üstinsan doğmuştur. Peki, neden yıldız-çocuk? Bunu da senaryonun yazım aşamasında Clarke ve Kubrick tarafından akşam yemeğine davet edilen Carl Sagan’dan bir alıntıyla cevaplayıp yazımı sonlandırmak isterim:&lt;/p&gt;&lt;blockquote style=&quot;margin-bottom: 1.25em; padding: 0px 0px 0px 1em; border-width: 0px 0px 0px 4px; border-style: solid; border-color: rgb(238, 238, 238); border-image: initial; font-style: italic; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 20px; line-height: 1.5em; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; quotes: none; color: rgb(123, 123, 123);&quot;&gt;&lt;p class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-bottom cf-tt-element-attached-center cf-tt-target-attached-top cf-tt-target-attached-center cf-tt-abutted cf-tt-abutted-top&quot; style=&quot;margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; text-rendering: optimizelegibility;&quot;&gt;“Hayatlarımız, geçmişimiz ve geleceğimiz, Güneş’e, Ay’a ve yıldızlara bağlıdır. Biz insanlar, atomların var ettiği doğayı ve onun heykeltıraşlığını yapan güçleri gördük. Bizler, evrenin bu kısmının gözleri, kulakları, düşünceleri ve duygularıyız ve sonunda, aslımızın ne olduğunu, kökenimizi merak etmeye başladık. Bizler, yani yıldız tozları, 10 milyar-milyar-milyarlarca atomun organize birlikteliğinden oluşan yıldızların ve sonunda bilince varan maddenin evriminin uzun soluklu sürecinde anlam bulduk, belki sadece Yeryüzü’nde, belki de tüm evrende… Biz tek bir türüz, yıldız ışığını toplayan yıldız tozuyuz.”&lt;/p&gt;&lt;/blockquote&gt;');
INSERT INTO `movie_reviews` (`id`, `entry_date`, `movie_name`, `review_title`, `card_image`, `card_content`, `content`) VALUES
(78, '2024-01-06 20:35:54', 'There Will Be Blood', 'Sahiden “İnsan Ne ile Yaşar”?', '../images/reviews/there-will-be-blood.jpg', '1898, çölün ıssız ve üstü kıt toprakları. Ancak yara kabuğu gibi sert ve aksi görünümün altında bir kimseye dünyaları bahşedecek değerdeki madenlerin serili olma umudu, Daniel Plainview’i harekete geçirmiştir. Genç adam katı toprağı hırsla kazarken dengesini kaybederek düşer ve ayağını kırar. Fakat böyle bir talihsizlik, Daniel’ın içindeki hırsı söndürebilecek, hatta onu azaltacak kudrette değildir. Ve genç adam arayışına devam eder. Sürünerek de olsa, terinin son damlasına kadar “ilerler.”', '&lt;p class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-center cf-tt-target-attached-center cf-tt-enabled cf-tt-out-of-bounds-top cf-tt-element-attached-top cf-tt-target-attached-bottom&quot; style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;1898, çölün ıssız ve üstü kıt toprakları. Ancak yara kabuğu gibi sert ve aksi görünümün altında bir kimseye dünyaları bahşedecek değerdeki madenlerin serili olma umudu, Daniel Plainview’i harekete geçirmiştir. Genç adam katı toprağı hırsla kazarken dengesini kaybederek düşer ve ayağını kırar. Fakat böyle bir talihsizlik, Daniel’ın içindeki hırsı söndürebilecek, hatta onu azaltacak kudrette değildir. Ve genç adam arayışına devam eder. Sürünerek de olsa, terinin son damlasına kadar “ilerler.” Bu sözde ilerleyiş, bir yandan vicdanı, ahlakı, erdemleri geriye götüren bir yola sürükler genç adamı. Daniel yoluna devam ettikçe objektifler, ardındaki çorak tepelere çevirir yönünü. Yol uzundur; ne vadettiği belli olmayan bir sonsuzluk kadar uzun… Kim kazanacaktır bu ölüm kalım savaşını? Hırsların kamçıladığı insan mı, yoksa davetkâr fakat bir o kadar haşin topraklar mı?&lt;/p&gt;&lt;p class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-center cf-tt-target-attached-center cf-tt-enabled cf-tt-out-of-bounds-top cf-tt-element-attached-top cf-tt-target-attached-bottom&quot; style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/there-will-be-blood-2.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2022/04/2593.webp&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Upton Sinclair’in&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Oil!&amp;nbsp;&lt;/em&gt;(1926-27) adlı romanından uyarlanan ve yönetmen koltuğunda Paul Thomas Anderson’ın yer aldığı&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;There Will Be Blood&amp;nbsp;&lt;/em&gt;(2007), bu açılış sahnesiyle aslında hem bir soru işaretini başlatır hem de filmde yaşanacaklara dair önemli ipuçları bırakır. Umutların isteklere, isteklerin arzulara ve arzuların da hırslara dönüştüğü insanlık hikâyesi, giriş sahnesindeki bu insan-doğa yarışıyla dahi özetlenmiştir bir bakıma. Daniel’ın başlangıçta gümüş arayışı ile çıktığı yol, daha sonra büyük petrol kuyularına doğru evrilir. Bu uğurda petrol bulundurma potansiyeli olan her karış toprağı satın almaya çalışmış, sahip olduklarını adım adım kazmıştır. İnsanın bu serüveninde doğanın bir destekçi değil de karşıt taraf olarak yansıtılması, bizleri eko-eleştirel bir film okumasına davet eder. Ancak hem filmin uyarlandığı romanın kaleme alınma tarihi hem de filmin, saldırı amacıyla doğrudan doğayı hedef almamış olması, filmin ekolojik bir bilinç çerçevesinde işlenmediğini ortaya koyar. Dolayısıyla yapılacak bir eko-eleştiri, ancak bu bilincin yoksunluğunu ortaya koymakla yetinecektir: Ekolojik bilince sahip olmayan bir kurguda doğa, sömürgeci Batı medeniyeti için kurban edilen/gözden çıkarılan bir bağlamdan öteye gidemez.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Bu argüman, aynı zamanda 20. yüzyılın başlarında ekolojik bilincin neden oluşamadığını da kendi içinde itiraf eder. Sömürgeci politika, amaca giden yoldaki her unsuru harcamayı ilke edinmiştir. Verilen hasar, bu uğurda yok edilen doğal alanlar, toplumlar, halklar, kültürler, diller veya dinler, sömürgeci politikaların ancak ikinci derecede önem atfettiği unsurlardır. Birinci sırada ise daima maddi kaygılar yer alır. Doyurmanın mümkün olmadığı bir hırsla kamçılandıkça daha büyük araziler, doğal alanlar ve buralarda yaşayan toplum, gerekirse insana ilişkin her şeyle beraber yok edilebilir. Böylesi bir ilke de ekolojik bilinç bir yana, insan haklarına dahi aykırı pek çok duruma meşruiyet getirir. Daniel’ın gümüş işini bırakıp dört yıllık bir süreçte petrol arayışına girmesiyle başlayan trajedi de yaşanacak sömürge hikâyesinin ne denli “kara” ve yok edici olacağını ima eder&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/there-will-be-blood-3.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2022/04/There-Will-Be-Blood-1.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;/a&gt;Özetle gümüşçülüğün ardından petrol işine soyunan Daniel, bu alanda kısa sürede isim yapar. Talihsiz bir kaza sonucu çalışanlarından biri ölünce işçinin oğlunu evlat edinir ve küçük çocuğa kendi dünyasının bakış açısını ince ince işlemeye başlar. Bir gün Paul Sunday adlı bir adam, yaşadığı Little Boston bölgesinde petrol olduğu bilgisini Daniel’e satınca hırslı adam, hedeflerini Güney Kaliforniya’ya doğrultur. Bölgede Paul’ün kardeşi rahip Eli Sunday’in -sözüm ona- egemenliğindeki muhafazakâr bir toplum yaşamaktadır. Eli, ancak kiliseye yardım yaptığı takdirde Daniel’a bölgenin topraklarını satacağını söyler. Böylelikle halkın dine bağlı sosyal yapılanmasını çözümleyen Daniel, Katolik Kilisesi’nin resmi öğretisi olan endülijans (indulgence) sistemini anımsatan bir yaklaşım getirir. Kendisine satılacak toprakların karşılığında halka yollar, okullar, imarethaneler, kiliseler vadeder. Dinin, büyülü bir dille süslenmiş manipülasyonları yoluyla kazandığı yaptırım gücü, Daniel’in kasaba meydanlarında yaptığı coşkulu konuşmalarda açık şekilde görülmektedir. Bu konuşmalar sırasında Daniel, halkın içinde idealize edilmiş bir medeniyet arzusunu kabartır. Oysa genç adam, verdiği bu sözlerin hiçbirini tutma niyetinde değildir. İyi bir kurgucu ve hikâye anlatıcısıdır yalnızca. Ne ki Eli, bölgede gerçekten petrol olduğunu bilmektedir ve Daniel’in önerdiği düşük fiyatları reddeder. Böylelikle tartışma, kilise ile sömürgeciler arasında bir toprak kavgasına dönüşür.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Daniel her seferinde kilisenin uygulamalarını küçümser. Kiliseye yaptığı bir ziyaret sırasında Eli’ın şeytan çıkarma ayini, Daniel’de topluma karşı bir iğrenme ve nefret başlatır. Bu sırada petrol arama çalışmalarında yaşanan bir kaza, bir işçinin ölümüyle sonuçlanır. Daniel, bu durum için doğrudan Eli’ı suçlar. Dinin, insanları koruyamadığını iddia eder. Çok geçmeden yaşanan bir başka kaza da Daniel’ın iddiasını destekler. Bir patlama şeklinde gerçekleşen kazada bakımını üstlendiği üvey evladı, işitme yetisini kaybetmiştir. Fakat bunun bir talihsizlik mi yoksa küçük çocuğun menfaatine bir durum mu olduğu, Daniel’ın gittikçe kabaran hırsına bakarak sıkça sorgulanır. Zira sağır olan çocuk, bundan böyle babasının manipülasyonlarına maruz kalmayacaktır. “Petrol karası kirli kelimeler”i duymayacak, kendini bu dimağdan koruyacaktır. Nitekim sağır olmasının ardından küçük çocuk için insanlığın yeniden kazanıldığı, erdemlere ait kavramların yeniden yazıldığı bir hayat başlar. İronik bir şekilde çocuk, işitme yetisini kaybederek sömürgeci dimağa maruz kalmaktan kurtulurken Daniel petrol karası hırsıyla gitgide körleşmektedir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Gözlerin gittikçe karardığı bu bölümde Tolstoy’un&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;İnsan Ne ile Yaşar&amp;nbsp;&lt;/em&gt;adlı novellasının derin izlerini görmek mümkündür. Tıpkı oradaki başkahraman gibi Daniel, petrol yataklarının bulunduğu toprakları elde etmek için her şeyi göze almış, varını yoğunu ortaya koymuştur. Bu uğurda üvey evladını bir yurda göndererek ayak altından uzaklaştırmayı dahi göze alır. Yıllar yılı öz çocuğu gibi sahiplendiği kişiden, bir “eşyadan kurtulur gibi” ayrılabilmesi, sömürgeci politikanın bir başka sonucuna işaret eder: metalaştırma. Buna göre tıpkı Little Boston toprakları gibi insanlar, dinler, inançlar metalaştırılarak alışveriş pazarının tezgâhına konabilir, sahip olunabilir, sahibin dileğince tasarruf edilebilir. Böyle bir hırsın zincirlerine bağlanmış bir insansa şüphesiz, gözlerini kör eden vaatler için yaşar.&lt;/p&gt;&lt;p style=&quot;text-align: center; margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;../images/reviews/there-will-be-blood-4.jpg&quot; style=&quot;width: 50%;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2022/04/intro-1648219588.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Filmin gönderme yaptığı bir diğer önemli eserse John Steinbeck’in&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;İnci&lt;/em&gt;’sidir. Romanda hayatlarına bir anda dahil olan değerli bir inciyle birlikte yoksul Santiago ve ailesi, günden güne incilerinin kölesi hâline gelir. Sonunda tabir caizse “dini imanı inci” olan aile, çocuklarını kaybetmiş, dağılmış, paramparça ve hiçbir şeysiz kalmıştır. Nitekim Daniel için de süreç buna benzer ilerler; filmin başında yöneltilen soru, böylelikle cevabına ulaşmaya başlar. Siyah petrol, Daniel’ın incisidir bir bakıma. Ona sahip olma tutkusuyla etrafındaki her şeyi harcayıp tüketirken bir yandan artan mal varlığı, erdem ve ahlak yönünden tükenmişliğini perdeler. Ne ki filmin sonunda aynı para ve mal varlığı hırsına kapılmaktan kendini kurtaramayan bir tek o değildir. Eli de kilise için yapılan yardımları kumarda kaybetmiş ve Daniel’den para istemeye gelmiştir. Bu olay, maddi çıkarlar için bir zamanlar kilise ile bağlarını tamamen koparmış olan Daniel için intikam fırsatıdır. Rahipten bağırarak tanrıyı reddetmesini söyler ve tatmin olana kadar bunu defalarca tekrarlatır. Sonunda da dizleri üzerine çöküp haysiyetini yerlere seren adamı bowling lobutuyla öldürür. Petrol karası bir kan sızar yere. Daniel cesedin başında tek başına oturur, “&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;İşim bitti,&lt;/em&gt;” der. Hayatında karşısına engel olan insanları yok etmiş, satın aldığı toprakları parçalamış, sevgisini ve saygısını kazanan kimseyi yanında tutamamıştır. Petrol işini tamamlamıştır pekâlâ, ancak ilk sahnede olduğu gibi kendi bedenini kırmış, kanını masumların bedeninden akıtmıştır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Daniel, sömürgeci toplumların politikalarını ve yaptırımlarını tek bir bedende canlandırırken bugün maalesef onun gibi insanları geniş kitleler hâlinde dünyanın her karışında görüyoruz. Üstelik sömürge faaliyetlerinin bir sonucu, aynı zamanda “besini” olan kapitalizme dâhil olarak bu ateşi daha da büyütüyoruz. Bataklık bu denli büyüyüp geri dönülmez biçimde çoğu değeri elimizden almışken bu noktada yitirilenleri kurtarmanın bir yolu yok ne yazık ki. Ancak bir bataklıkta olduğumuzu hatırlamak, hiç değilse Daniel gibi kimselerin çevremize kurduğu büyülü, idealize dünyaların aslında başka amaçlara hizmet eden birer tuzak olduğunu görmemiz için en büyük adım. Belki daha fazla kan çıkmadan önce, içinde azılı hırsların gezdiği kanımıza sahip çıkmamız gerek&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;. O vakit insan, değerlerin madde ile ölçülemeyeceğini anlar. O vakit insan, insan ile yaşar, insanlık için yaşar.&lt;/em&gt;&lt;/p&gt;');
INSERT INTO `movie_reviews` (`id`, `entry_date`, `movie_name`, `review_title`, `card_image`, `card_content`, `content`) VALUES
(79, '2024-01-06 20:43:32', 'Les vacances de Monsieur Hulot', 'Bir Modern Kent İnsanı Eleştirisi', '../images/reviews/Les vacances de Monsieur Hulot.jpg', 'Jacques Tati (1907-1982), sinema kariyerini modernitenin toplum tarafından yanlış yorumlanması üzerine inşa etmiştir. Her ne kadar moderniteyi eleştiren bir yönetmen olarak algılanıp gelenekselci, muhafazakâr gibi yakıştırmalara maruz kalsa da aslında çoğunlukla yanlış anlaşılmıştır. Hatta bu duruma bizzat kendisi açıklama getirmiştir: “Eğer ben modern mimarlığa karşı olsaydım, en kötü binaları göstermeyi tercih ederdim”', '&lt;p class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-center cf-tt-target-attached-center cf-tt-enabled cf-tt-out-of-bounds-top cf-tt-element-attached-top cf-tt-target-attached-bottom&quot; style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Jacques Tati (1907-1982), sinema kariyerini modernitenin toplum tarafından yanlış yorumlanması üzerine inşa etmiştir. Her ne kadar moderniteyi eleştiren bir yönetmen olarak algılanıp gelenekselci, muhafazakâr gibi yakıştırmalara maruz kalsa da aslında çoğunlukla yanlış anlaşılmıştır. Hatta bu duruma bizzat kendisi açıklama getirmiştir:&amp;nbsp;&lt;em class=&quot;cf-tweet-this cf-tt-target cf-tt-abutted cf-tt-abutted-top cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-bottom cf-tt-element-attached-center cf-tt-target-attached-top cf-tt-target-attached-center&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Eğer ben modern mimarlığa karşı olsaydım, en kötü binaları göstermeyi tercih ederdim”&amp;nbsp;&lt;/em&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[1].&lt;/span&gt;&amp;nbsp;Oysa Tati, filmlerinde modernizmin en önemli eserlerinden ilham almıştır. Lakin öncelikle, sinema aracıyla inşa ettiği bu modern hayat eleştirisine kırsaldaki hayatı perdeye yansıtarak başlamıştır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tati‘nin ilk uzun metrajı&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Jour de fête / Bayram Günü&lt;/em&gt;&amp;nbsp;(1949) köyde geçen bir hicivdir. &amp;nbsp;İkinci filminde de kısmen kent hayatından uzak durmaya devam etmiştir. Lakin yine kırsalda geçen&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les vacances de Monsieur Hulot&lt;/em&gt;&amp;nbsp;/&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Bay Hulot’nun Tatili&lt;/em&gt;&amp;nbsp;(1953), modern kentlerden tatil yapmak amacıyla gelen insanlara odaklanmasıyla ilk filminden farklı bir yerde durmaktadır. Bu anlamda Tati, bir sonraki filmi olan&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle / Amcam’&lt;/em&gt;da (1958) kentleri yavaş yavaş ele geçiren modern hayatın bireyleriyle seyirciyi tanıştırmıştır. Zira&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Amcam&lt;/em&gt;’dan sonra gelen&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Play Time / Oyun Vakti&lt;/em&gt;’nde (1967) geleneksel hayata dair hiçbir şey kalmayacaktır. Tati, filmografisi üzerinden Avrupa insanının adım adım geleneksel hayattan nasıl modern hayata teslim olduğunu gözler önüne sermektedir. Bu noktada&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les vacances de Monsieur Hulot&lt;/em&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;,&amp;nbsp;&lt;/em&gt;Tati’nin uzun yıllara yaydığı meselesinin en önemli ayaklarından biridir. Zira Tati’nin bizzat kendisinin kamera karşısına geçerek Mösyö Hulot’ya hayat verdiği de ilk filmidir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/bay-hulot-1.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-full wp-image-141418 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/bay-hulot-1.jpg&quot; alt=&quot;&quot; width=&quot;1200&quot; height=&quot;675&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/bay-hulot-1.jpg 1200w, https://filmhafizasi.com/wp-content/uploads/2023/08/bay-hulot-1-300x169.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/08/bay-hulot-1-1024x576.jpg 1024w, https://filmhafizasi.com/wp-content/uploads/2023/08/bay-hulot-1-768x432.jpg 768w&quot; sizes=&quot;(max-width: 1200px) 100vw, 1200px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Flâneur&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(2)&lt;/sup&gt;&lt;/span&gt;, sığınağını kitlede arar”&lt;/em&gt;&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[2].&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Mösyö Hulot’nun tarzı,&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les vacances de Monsieur Hulot&lt;/em&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;’ta&lt;/em&gt;&amp;nbsp;her ne kadar tam olarak oturmamış olsa da genel hatlarıyla üzerindeki kıyafetten tutun da jet ve mimiklerine kadar birçok şey sonraki filmlerde de hiç değişmeyecektir. Mösyö Hulot, sessiz sinema dönemindeki özdeşlerinden Şarlo’nun uzun ve fazlasıyla bol pantolonlarının aksine dar ve kısa pantolonları, kısa pantolonun altından gözüken çizgili çorapları, yine Şarlo’nun kocaman burnu olan siyah deri ayakkabılarının aksine oldukça sade beyaz bez ayakkabıları, asla ağzından düşürmediği piposu ve Şarlo’nun melon şapkasına kıyasla çok daha gösterişsiz balıkçı şapkası ile oldukça sempatik ve sessiz sinema dönemi tiplemelerine göre daha modern bir çizgide varlık göstermektedir. Bu ilk varlık gösterdiği film yaz mevsiminde geçtiği için henüz Mösyö Hulot’nun pardösüsünü sırtına geçirmediğini ve her fırsatta yanından eksik etmediği şemsiyesinin yerine sıklıkla tenis raketi taşıdığı görülmektedir. Fakat Mösyö Hulot’nun dış görünüşü itibariyle Şarlo veya diğer sessiz dönemde varlık göstermiş karakterlerin aksine daha az dikkat çeken ve yavaş yavaş kendini tekrar etmeye de başlayan modern kent insanlarıyla çok uyumlu bir tercih yaptığı fark edilmektedir. Zira Mösyö Hulot, kalabalıklar içinde dikkat çekmeyi değil aksine dış görünüşüyle diğerleriyle uyumlu olmayı amaçlamaktadır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Bir pantomim sanatçısı da olan Tati’nin tıpkı Buster Keaton’un veya Chaplin’in karakterleri gibi beyaz yüz makyajıyla kamera karşısına geçmesi olası bir tercih olabilirdi. Fakat Tati, Mösyö Hulot’nun görünüş itibariyle kalabalıklara karışmasını, tektipleştirilmiş modern insan kalabalığında gözden kaybolmasını amaçlamaktadır. Zira Mösyö Hulot, gün boyu kentleri dolaşıp, kalabalıkları gözlemlemesi ile bir flâneur olarak da pekâlâ tanımlanmaktadır. Gel gör ki Mösyö Hulot, görünüşte yakaladığı aynılığı ne davranışlarında ne de karakterinde göster(e)memektedir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3.png&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-full wp-image-141419 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3.png&quot; alt=&quot;&quot; width=&quot;1860&quot; height=&quot;1044&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3.png 1860w, https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3-300x168.png 300w, https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3-1024x575.png 1024w, https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3-768x431.png 768w, https://filmhafizasi.com/wp-content/uploads/2023/08/Monsieur-Hulots-Holiday_3-1536x862.png 1536w&quot; sizes=&quot;(max-width: 1860px) 100vw, 1860px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Buster Keaton’ın Tati’den daha iyi bir takipçisi yoktur. Bitimsizce yinelenen kalıplar ve çekingen, yenilikçi vurgular karşı konulmaz bir mizahın kaynağı haline gelir. Öncellerinin filmlerinde olduğu gibi her jest özgündür ve istenç ya da genel uygulamanın gerektirdiğinden farklıdır&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[3].&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Sinemadan önce pantomim sanatıyla da ilgilenmiş olan Tati, Mösyö Hulot’a kendine özgü jet ve mimikler kazandırmıştır. Öne doğru eğimli, parmaklarının ucunda yükseliyormuşçasına yürüyüşü ve adeta bedeniyle konuşuyormuşçasına yaptığı estetik hareketleriyle sadece rol yapmayıp aynı zamanda pantomim sanatını icra etmektedir. Mösyö Hulot’nun perdede göründüğü anların hepsinde yüzünün beyaz makyajlı, ellerinin de beyaz eldivenli olduğu, arka fonun ise simsiyah olduğu düşünüldüğünde perdede bir pantomim gösterisinin olduğunu hayal etmek mümkündür. Bu anlamda da yine sessiz dönem tiplemelerinin slapstick&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(1)&amp;nbsp;&lt;/sup&gt;&lt;/span&gt;hareketlerinden farklılık taşımaktadır. En basit haliyle kaba komedi olarak tanımlanabilecek olan slapstick komediye göre daha estetik bir yerde durmaktadır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Üstelik Tati filmleri, sinemanın sesli döneminde yaratmıştır. Lakin Tati filmleri sesli döneme ait olsa da diyaloglardan beslenmeyen yapımlardır.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les vacances de Monsieur Hulot&lt;/em&gt;&amp;nbsp;da çok az diyaloğun olduğu bir filmdir. Mösyö Hulot’nun filmde ağzından çıkan tek anlamlı replik ise otele ilk geldiğinde kayıt yapılması için ismini zikretmesidir. İsmini kodladığı anlar, Tati’nin diğer filmlerinde perdede varlık gösterecek olan Möstö Hulot’nun bir anlamda seyirciye sunulmasıdır.&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-full wp-image-141420 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle.jpg&quot; alt=&quot;&quot; width=&quot;1920&quot; height=&quot;1080&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle.jpg 1920w, https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle-300x169.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle-1024x576.jpg 1024w, https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle-768x432.jpg 768w, https://filmhafizasi.com/wp-content/uploads/2023/08/2_Les_Vacances_1953_Les_Films_de_Mon_Oncle-1536x864.jpg 1536w&quot; sizes=&quot;(max-width: 1920px) 100vw, 1920px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Bugün yorgunluk günüdür. Ara-zaman, işin olmadığı oyun-zamanıdır. Bu zaman Heidegger’in, esasen ihtimam gösterilen ve iş için olan zamanından farklıdır. Handke bu ara-zamanı bir arkadaşlık zamanı olarak tarif eder”&amp;nbsp;&lt;/em&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[4].&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Küçük bir sahil kasabasında geçen Mösyö&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;Hulot’nun Tatili,&amp;nbsp;&lt;/em&gt;sezonun açılışıyla kasabadaki otele gelen misafirlerin yolculuğuyla başlayıp yine sezonun sona erip herkesin bir sonraki yıl görüşmek üzere sözleşip otelden ayrılmasıyla son bulmaktadır. Bu mesire yerinde mevsimin yaz olduğunu çıkarsamak kolay olsa da filmde geçen süreyi kestirmek pek mümkün değildir. Fakat zamanın da pek bir önemi zaten yoktur. Çünkü film ne bir olay anlatmakta ne de herhangi bir karakterin dönüşümüne odaklanmaktadır.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les vacances de Monsieur Hulot&lt;/em&gt;, tıpkı Tati’nin tüm filmografisinde olduğu gibi duruma odaklanmakta; gündelik hayatın rutinini ön plana çıkarmaktadır. Her öğlen aynı saatte otel müşterilerini yemeğe çağıran zil sesi, Mösyö Hulot’nun hiç teklemeden her gün bozulan antika arabası, yine Mösyö Hulot’nun her gece tüm oteli bir şekilde ayağa kaldırması, otel sakinlerinden bir iş insanına rutin olarak gelen telefonlar, bir çiftin her gün sabahtan akşama kadar sadece çevreyi dolaşması ve daha nicesi… Film, 21. yüzyıl filozoflarından Byung-Chul Han’ın ifade ettiği “yorgunluk günü”nü tatilde dahi es geçen, günlük rutinin esiri olan üst orta sınıf kent sakinlerinin sıradanlığının bir eleştirisidir. Tati, modernitenin kıskacındaki kent insanını sadece dış görünüşte değil yaşantı anlamında da sıradanlaştığını gözler önüne sermektedir. Ki filmin prolog sahnesi bu anlamda oldukça önemlidir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Bir tren garında başlayan filmde yolcuların sürü halinde hareket ettiği, sistematik bir şekilde aynı ritmik hareketleri yaptıkları, birbirinden ayırt edilemeyen sesler çıkardıkları seyredilmektedir. Yine otobüse binen yolcuların, denize giren veya yemek yiyenlerin hep benzer bir davranış kalıbı içinde olduğu görülmektedir.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Modernleşme çabasının gündelik hayatta ürettiği yapay ve monoton ilişkilerin dönemin gerçekliğiyle ilişkisini sorgulayan ve bireyin topluma adaptasyonunu irdeleyen Tati’nin”&lt;/em&gt;&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[5]&lt;/span&gt;&amp;nbsp;hayat verdiği Mösyö Hulot ise her haliyle bu kalıbın içine gir(e)memektedir. Mösyö Hulot, Byung-Chul Han’ın bahsettiği “oyun zamanı”nı her fırsatta hayata geçirir. Maskeli baloya kimse katılmaya bile tenezzül etmezken o kendine bulduğu arkadaş ile balonun tam olarak keyfini çıkarır. Tıpkı tüm tatilin olduğu gibi. Filmde bu sistematik çizgiyi Mösyö Hulot kadar olmasa da aynı şekilde takip etmeyen birkaç karakter daha ön plana çıkmaktadır. Tatilcilerin geneline göre daha gözlemci olan ve Mösyö Hulot’nun da farkında olup onunla zaman geçirmekten zevk alan genç kadın, tüm tatil boyunca eşinin arkasından onu takip ediyormuş gibi görünse de aslında ondan ve onun klişe davranışlarından hiç haz etmeyen adam, Mösyö Hulot’nun tenis oynama stilinden çok etkilenen yaşlı kadın, yetişkinlerin arasında kendine eğlenceli uğraşlar bulma peşinde olan çocuk gibi. Bu karakterlerin hepsi de Mösyö Hulot’ya önem veren kişiler olması açısından da diğer tatilcilerden ayrı yerde durmaktadır. Fakat günün sonunda tüm kalabalıktan her anlamda belirgin farkları olan Mösyö Hulot’tur. Çünkü Mösyö Hulot, sınıfsal anlamda da diğerlerinden farklıdır.&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/lesvacancesdemhulot_1.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-full wp-image-141421 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/lesvacancesdemhulot_1.jpg&quot; alt=&quot;&quot; width=&quot;870&quot; height=&quot;400&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/08/lesvacancesdemhulot_1.jpg 870w, https://filmhafizasi.com/wp-content/uploads/2023/08/lesvacancesdemhulot_1-300x138.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/08/lesvacancesdemhulot_1-768x353.jpg 768w&quot; sizes=&quot;(max-width: 870px) 100vw, 870px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Tati’nin güldürü anlayışının roman ile yer değiştirdiğini söyleyebiliriz. Romanda hikâyenin içine karışmış her şey yazarın iç dünyasında kazandıklarıdır. Gerçekten, Tati güldürü unsurlarını minumum ölçüde ve yavaşça kullanır. Her güldürü unsurunu entellektüel bir biçimde kamçılandırır, şekillendirir, karakterlerini belirsizlik ile doldurur. Bu da karakterler için hayatı zorlaştırır”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[6].&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Filmin çekildiği dönemde bile antika olarak kabul edilebilecek külüstür arabası, otelin diğer odalarından farklı olan çatı katında kalması, daha çok üst ve üst orta sınıfın uğraştığı bir spor dalı olan tenisi oynamayı hiç bilmemesi veya ata binmekle ilgili hiçbir fikrinin olmaması, sürekli bir şeyleri yanlış yapması gibi birçok gösterge onun alt sınıftan olduğunu ortaya koymaktadır. İşte tüm bu nedenlerle de her ne kadar oldukça centilmen ve yardımsever biri olmasına rağmen toplum tarafından dışlandığı görülmektedir. Bu dışlanma özellikle filmin finalinde ortaya çıkmaktadır. Otel sakinleri birbiriyle vedalaşıp birbirlerine gelecek yıl için sözler verirken Mösyö Hulot’yu görmezlikten gelirler. Fakat Mösyö Hulot’nun bu aykırılık hâli kimi zaman onun avantajına da dönüşmektedir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Böylece Mösyö Hulot, o modern insan güruhunun içine karışmaktansa tam anlamıyla görünmez olur. Oysa Mösyö Hulot, onlar gibi giyinip onlarla aynı otelde kalıp, aynı sporu oynar. Ne var ki bunlar cemiyete kabul edilmesi için yeterli gelmez. Çünkü Mösyö Hulot, biraz “değişik” biridir. Örneğin tenis oynamayı bilmeyen Mösyö Hulot’nun raketi farklı bir şekilde kullanması rakiplerinin hepsine galip gelmesini sağlar.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Lakin bu durum onun daha da dışlanmasını ve daha da yalnızlaşmasını beraberinde getirir. Toplum hep yaptığı gibi sürüye tam olarak ayak uyduramayanı görmezlikten gelmeyi, dışlamayı, yok saymayı tercih etmiştir. Üstelik Mösyö Hulot’nun yeni model bir arabası, yeni moda davranışları yoktur. Oysa moderniteyi yanlış yorumlayan diğerleri sürekli tüketimin, birbirinin tıpatıp aynısı giyinip konuşmanın, aynı şeylerle eğlenip aynı şeyleri konuşmanın modernlik olduğunu düşünmektedir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tati,&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les vacances de Monsieur Hulot’da&lt;/em&gt;, modern kent insanının en büyük eğlencelerinden biri olan yaz tatilini olabilecek en sıkıcı hâliyle perdeye yansıtır. Modernitenin eğlence olarak sunduğu tüketim alışkanlığını büyük bir sıradanlıkla ortaya koyan Tati, tüm bu sıradanlığın ortasına bir alternatif yaratmış ve bu alternatif birey üzerinden toplumun yüzüne bir ayna tutmak istemiştir. Bu ikinci uzun metrajlı filminde&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“1950-1970 yılları arası Fransa’da modern kent hayatındaki hızlı dönüşümü, geleneksel hayat biçimini sürdüren ana karakter Mösyö Hulot’nun kent (…) mekânlarıyla kurmakta zorlandığı etkileşim üzerinden eleştiren Tati sinemasında, tüketim kültürü ve geleneksel hayat çatışmasında gelişen kent morfolojisindeki mekânsal karşıtlıklar”&lt;/em&gt;&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[7]&lt;/span&gt;&amp;nbsp;yerine daha çok o mekânları işgal eden kalabalıkların durumu perdeye yansıtılmaktadır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Jacques Tati’nin bir sonraki filmi&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle / Amcam&lt;/em&gt;&amp;nbsp;(1958) filminin analizine&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/geleneksel-ile-modernitenin-sessiz-savasi-mon-oncle-1958/&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;buradan&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&amp;nbsp;ulaşabilirsiniz.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Dipnot:&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;(1)&lt;/span&gt;&amp;nbsp;Özellikle sessiz sinema döneminde Charlie Chaplin, Buster Keaton, Laurel ve Hardy gibi komedi sanatçılarının abartılı ve dikkatsizce yaptıkları oyunculuğa verilen isimdir. Örneğin muz kabuğuna basıp düşmek, birine vurmak isterken bir başkasına vurmak gibi…&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;(2)&lt;/span&gt;&amp;nbsp;Flâneur, dönemin birçok düşünürünün ifade ettiği gibi “amaçsızca, rastgele yürüyerek dolaşan ve kendini çevresinin etkisine bırakıp ‘anın izini süren kişi’ olarak”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[8]&lt;/span&gt;&amp;nbsp;tanımlanabilir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Kaynakça:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[1]&lt;/span&gt;&amp;nbsp;Atacan, A. (2012). Modern Mimarlıkta Yabancılaşma Sorunu; Jacques Tati Filmleri. Gazi Üniversitesi Fen Bilimleri Enstitüsü. Ankara.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[2]&amp;nbsp;&lt;/span&gt;Benjamin, W. (2022). Pasajlar. (Cemal, A. Çev.). Yapı Kredi.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[3]&amp;nbsp;&lt;/span&gt;Biro, Y. (2011).&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Sinemada Zaman/ Ritmik Tasarım; Türbülans ve Akış&lt;/em&gt;. Birinci Basım. (Altunkanat, A.C. Çev.). Doruk Yayımcılık.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[4]&amp;nbsp;&lt;/span&gt;Chul Han, B. (2021). Yorgunluk Toplumu. (Yalçın, S. Çev.). Metis Yayıncılık.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[5]&lt;/span&gt;&amp;nbsp;Aslan, G. &amp;amp; Masatlıoğlu, S. E. (2023). Sinema-mimarlık arakesitinde alternatif bir öğrenme ortamı: Kent morfolojisinin endüstrileşmiş kültürle ilişkisinin Tati sinemasındaki modernleşme eleştirileri üzerinden incelenmesi.&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;Türkiye Kentsel Morfoloji Ağı, (799-807).&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[6]&lt;/span&gt;&amp;nbsp;Chion, M. (2002). The Films of Jacques Tati, 2nd ed.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Guernica Editions&lt;/em&gt;, Canada. 8,16,17.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[7]&lt;/span&gt;&amp;nbsp;Aslan, G. &amp;amp; Masatlıoğlu, S. E. (2023). Sinema-mimarlık arakesitinde alternatif bir öğrenme ortamı: Kent morfolojisinin endüstrileşmiş kültürle ilişkisinin Tati sinemasındaki modernleşme eleştirileri üzerinden incelenmesi.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Türkiye Kentsel Morfoloji Ağı,&lt;/em&gt;&amp;nbsp;(799-807).&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[8]&lt;/span&gt;&amp;nbsp;Canpolat, E. (2014). Walter Benjamin’de Yöntem ve Flâneur. Moment Dergi. Hacettepe Üniversitesi İletişim Fakültesi Kültürel Çalışmalar Dergisi 2014, 1(2): 270-295.&lt;/p&gt;');
INSERT INTO `movie_reviews` (`id`, `entry_date`, `movie_name`, `review_title`, `card_image`, `card_content`, `content`) VALUES
(80, '2024-01-06 20:46:22', 'Mon Oncle', 'Geleneksel ile Modernitenin Sessiz Savaşı', '../images/reviews/mon oncle.jpg', 'Jacques Tatischeff (1902-1987); yirminci yüzyılın neredeyse hepsine vakıf olmuş bir düşünür, yönetmen, senarist, pandomim sanatçısı, mühendis, oyuncu ve daha nicesidir. Yaşadığı dönemin aksine gücünü yavaşlıktan ve özenden almış bir yaratıcıdır. Uzun ömrüne sayılı film sığdırmıştır. Zira filmlerini incelikle planlamış ve tasarlamıştır.', '&lt;h4 style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;span class=&quot;cf-tweet-this cf-tt-target cf-tt-enabled cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-out-of-bounds-top cf-tt-element-attached-top cf-tt-element-attached-center cf-tt-target-attached-bottom cf-tt-target-attached-center&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Kameranın Arkasındaki İllüzyonist: Bir Pandomim Sanatçısı Jacques Tatischeff&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/h4&gt;&lt;p class=&quot;cf-tweet-this cf-tt-target cf-tt-out-of-bounds cf-tt-out-of-bounds-bottom cf-tt-element-attached-bottom cf-tt-element-attached-center cf-tt-target-attached-top cf-tt-target-attached-center cf-tt-abutted cf-tt-abutted-top&quot; style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Jacques Tatischeff (1902-1987); yirminci yüzyılın neredeyse hepsine vakıf olmuş bir düşünür, yönetmen, senarist, pandomim sanatçısı, mühendis, oyuncu ve daha nicesidir. Yaşadığı dönemin aksine gücünü yavaşlıktan ve özenden almış bir yaratıcıdır. Uzun ömrüne sayılı film sığdırmıştır. Zira filmlerini incelikle planlamış ve tasarlamıştır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tati, filmlerinin senaryosunu yazıp yönetmekle kalmamış,&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les Vacances de Monsieur Hulot&amp;nbsp;&lt;/em&gt;(1953) ile birlikte sinemada yarattığı evrenine Mösyö Hulot karakterini de dâhil etmiştir. Modernist yaşamı eleştirdiği en önemli üç filmi&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle&amp;nbsp;&lt;/em&gt;(1958),&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;Play Time&amp;nbsp;&lt;/em&gt;(1967) ve&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Traffic&lt;/em&gt;&amp;nbsp;(1971), Mösyö Hulot karakteri üzerinden ilerlemektedir. Tati’nin başarıyla hayat verdiği Mösyö Hulot karakteri, yine birer modern yaşam hicivcisi olarak tanımlayabileceğimiz Charlie Chaplin’in yarattığı Şarlo veya Buster Keaton’ın hayat verdiği karakterlerle ortaklık taşımaktadır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Lakin Mösyö Hulot, Şarlo gibi slapstick&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;1)&lt;/span&gt;&lt;/sup&gt;&amp;nbsp;hareketlerden ziyade bir pandomim sanatçısı da olduğundan dolayı daha sakin ve tasvir edici bir tavırla hareket eder. Mösyö Hulot’nun, Şarlo’nun aksine sesli dönem karakteri olsa da tam olarak konuştuğunu iddia edemeyiz. Sadece bazı homurtuya benzeyen sesler çıkaran Mösyö Hulot, sözleriyle değil de hareketleriyle mim sanatını icra eder.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Fakat Tati, her ne kadar bir güldürü ustası olsa da sürekli komedi unsurlarından beslenmemektedir. Tati filmleri, daha çok durum komedisi olarak tanımlanabilir. Tati, her ne kadar çoğunlukla modernizme karşı olan hatta ve hatta muhafazakâr bir sinemacı olarak görülse de durum pek öyle değildir. Tati, modernizme değil onun uygulanış biçimine karşı çıkmıştır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tati ile ilgili bahsetmemiz gereken en önemli şey ise filmlerini gerçek mekânlarda çekmemesidir. “Daha Lumière’in filmleriyle birlikte Paris, cadde ve bulvarlarıyla, ‘toplum dışı’ ve aylak insanlarıyla sinematografik bir sunum, canlı bir dekor olmuştu”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[1]&lt;/span&gt;&amp;nbsp;fakat Tati, bu canlı dekoru kullanmaktan imtina ederek kendi dekorunu yaratmıştır. Birçok mimar ile birlikte çalışarak adeta kendi alternatif kentlerini ve yapılarını yaratmıştır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Jacques Tati,&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle&lt;/em&gt;’da (1958) modern yaşama tam anlamıyla teslim olmak üzere olan Paris’i tabiri caizse ortadan ikiye bölmüştür. Bir tarafta hâlâ geleneksel yaşamı koruyan mahalleler, diğer tarafta ise kentin değişen yüzünü temsil eden büyük malikâneler… Tüm film boyunca peşinden bir an bile ayrılmayacağımız Mösyö Hulot’nun yaşadığı geleneksel taraf ile Mösyö Hulot’nun kız kardeşi, yeğeni ve eniştesinin ikamet ettiği Arpel Malikânesi’ni yıkılmakta olan alelade bir duvar ayırmaktadır. Yıkılmakta olan duvar;&amp;nbsp; hâlâ yapımı tamamlanmamış, şantiye alanı gibi olan kentin bir metaforudur. Mösyö Hulot, kent henüz tam olarak dümdüz edilip de yerine yeni ve aşılmaz bir duvar yapılmadığı için her iki taraf arasında kolaylıkla yolculuk yapabilmektedir.&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-large wp-image-138754 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati-1024x768.jpg&quot; alt=&quot;&quot; width=&quot;1024&quot; height=&quot;768&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati-1024x768.jpg 1024w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati-300x225.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati-768x576.jpg 768w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati-1536x1152.jpg 1536w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-jacques-tati.jpg 2000w&quot; sizes=&quot;(max-width: 1024px) 100vw, 1024px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h4 style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Unutulmak Üzere Olan Bir Sanat Eseri: Geleneksel Paris&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;“Geleneksel kenti, toplumsalın, siyasalın, refahın, bilginin, karşılaşmaların, oyunun, eğlencenin, sanatın üretildiği bir “oeuvre” (sanat eseri) olarak tanımlayan Lefebvre&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;2)&lt;/span&gt;&lt;/sup&gt;, bu sanat eserinin, kent sakinlerince -vatandaşlık bağı aramadan mülteciler de dâhil- kolektif olarak yaratıldığını öne sürer”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[2]&lt;/span&gt;. Mösyö Hulot’nun yaşadığı geleneksel taraf; iç içe geçmiş, çarpık ve rengârenk evleri, simetriden nasibini almamış düzeni, insanların buluşup sosyalleşebilecekleri küçük alanları, dar sokakları ile tam olarak Georges-Eugéne Haussmann’ın&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;3)&lt;/span&gt;&lt;/sup&gt;&amp;nbsp;kenti yıktırmadan önce çektirdiği fotoğraflardakine ve Lefebvre’nin tasvirindeki haline benzer. Geleneksel tarafta asla evlerin içerisine girilmez. Zira herkes kamusal alandadır. Kadını, erkeği ve çocuğuyla tüm bireyler özgürce kentin kamusal alanında varlık gösterebilirler. Geleneksellik, her ne kadar muhafazakârlığı akla getirse de kadını domestik alana hapsetmek isteyen zihniyete teslim olmamıştır. Bir pazar alanında gördüğümüz insanlar, alışveriş yaparken ya da çalışırken aynı zamanda sohbet ederler. Mahalle alanları, modern Paris’in en önemli simgelerinden biri olan sandalye ve masaları caddeye veya sokağa doğru bakan kafelerin görevini fazlasıyla gören yerlerden biridir. Parası olanın da olmayanın da, çocuklunun da bekârın da, kadının da erkeğin de özgürce varlık gösterebileceği bir mekândır. Geleneksel Paris, sadece mimari yapısından dolayı bile daha özgürleştirici bir yerdedir. Kadın tahakküm altına alınmamış, çocuklar köleleştirilmemiş, kitle birbirine yabancılaştırılmamıştır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Çocukların gün boyu sokaklarda oynayıp, afacanlık yaptığı geleneksel tarafın en belirgin özelliği ise steril bir profil çizmemesidir. Sokaklar kirlidir. Zira temizlik görevlileri sosyalleşmekten işlerini yapmaya vakit bulamamaktadır. Çocuklar hijyen kurallarının uygulanmadığı seyyar satıcılardan atıştırmakta, pazarda hiçbir kural olmadan her şey açıkta satılmaktadır. Üstelik tüm bunların yanında hayat neredeyse akmamaktadır. Çünkü çoğunlukla işler yapılmaz, yavaş yapılır veya fazlasıyla savsaklanır durumdadır. Bir nevi hayatın bile isteye yavaşlatıldığı bu evrende herkesin birbirine sonsuz güveni vardır. Geleneksel tarafta yaşayanlar, &amp;nbsp;21. yüzyıl filozoflarından Byung-Chul Han’ın “Bugün yorgunluk günüdür. Ara-zaman, işin olmadığı oyun-zamanıdır. Bu zaman Heidegger’in&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(4)&lt;/sup&gt;,&lt;/span&gt;&amp;nbsp;esasen ihtimam gösterilen ve iş için olan zamanından farklıdır. Handke&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(5)&lt;/sup&gt;&lt;/span&gt;&amp;nbsp;bu ara-zamanı bir arkadaşlık zamanı olarak tarif eder”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[3]&lt;/span&gt;&amp;nbsp;diye dile getirdiği ve modern toplum ile birlikte unutulan zamanı yaşamaktadırlar. Geleneksel hayatın özneleri, zamanın onları değil onların zamanı yönettiği bir hayatı yaşamaktadırlar.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tati, her şeyi fazlasıyla açık seçik ortaya dökerken asla tam anlamıyla geleneksel yaşamı övmez. Her ne kadar geleneksel tarafta iç kıpırdatan müzik kullanımı ve kuş sesleriyle bir sempati yaratmaya çalışsa da birçok şeyin aksadığını da tüm şeffaflığıyla gözler önüne serer. Mösyö Hulot, yaşadığı evrenin eksikliklerini gözlemleyip aktarmaktan sakınmaz. Lakin Tati, tüm film boyunca da çözüm önermekten uzak bir yerde durur.&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mononcle-arpelhulot.webp&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-large wp-image-138755 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mononcle-arpelhulot-1024x768.webp&quot; alt=&quot;&quot; width=&quot;1024&quot; height=&quot;768&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mononcle-arpelhulot-1024x768.webp 1024w, https://filmhafizasi.com/wp-content/uploads/2023/03/mononcle-arpelhulot-300x225.webp 300w, https://filmhafizasi.com/wp-content/uploads/2023/03/mononcle-arpelhulot-768x576.webp 768w, https://filmhafizasi.com/wp-content/uploads/2023/03/mononcle-arpelhulot.webp 1200w&quot; sizes=&quot;(max-width: 1024px) 100vw, 1024px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h4 style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Patriarkal Düzenin Bir İzdüşümü: Modern Paris&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;“Paris değişiyor, ancak benim melankolimde hiçbir şey yerinden kıpırdamadı!&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Yeni saraylar, yapı iskeleleri, bloklar.&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Eski varoşlar, tümü benim için bir alegoriye dönüşüyor,&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Ve benim sevgili anılarım kayalar kadar ağır”&lt;/em&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;[4]&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Charles Pierre Baudelaire&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Baudelaire her ne kadar hafızasını modern kente teslim etmemiş olsa da kent, yavaş yavaş teslim olmaktadır. Geleneksel taraftan modern tarafa geçtiğimizde öncelikle renklerin çekildiğini ve geriye sadece gri ve tonlarının kaldığını görürüz. Tati, her ne kadar geleneksel tarafta kamusal alana yer vermiş olsa da modern tarafı sadece bir ev ve iş yeri üzerinden aktarmayı tercih eder. Sokakları, bulvarları, caddeleri görmediğimiz modern tarafta en çok ilgiye mazhar olacak şey Arpel Malikânesi olmaktadır. Arpel Malikânesi, bahçesindeki havuzu, araba garajı, pencereleri ve daha birçok detayıyla tam anlamıyla eril bir birey olarak varlık göstermektedir. Dikey halde duran balık figürü şeklindeki fıskiye ya da garaj fallik obje, evin üst kadındaki yuvarlak pencereler ise göz olarak çizilmişlerdir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Eril bir birey olarak tanımladığımız Arpel Malikânesi’nin üst katındaki pencereleri, tam anlamıyla Fransız düşünür Michelle Foucault’un (1926-1984) panoptikon&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(6)&lt;/sup&gt;&lt;/span&gt;&amp;nbsp;teorisindeki kulede bulunup her hücreyi rahatlıkla gözetleyen “göz” olarak düşünebiliriz. Ki bu iki yuvarlak pencerenin her birine malikânenin bireylerinin geçip de komşu evi dikizlediği sahnede benzerlik en üst noktaya ulaşır. Açılıp kapanan panjur göz kapağını, pencerenin orta yerine yerleşen bireyler ise göz bebeğini temsil eder. Ayrıca Arpel ailesinin bireylerinin komşularını sürekli gözetim altında tutması, gözetleme kültürünün iktidarın en üst noktasından tabana kadar toplumun her katmanına sirayet ettiğini göstermektedir. Bu da hiç kuşkusuz akıllara yine Foucault’nun “İktidar her yerdedir” Sözlerini akla getirmektedir.&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle&lt;/em&gt;’da da iktidar, gözetleme ve gözetlenme durumunu kent sakinlerine benimseterek meseleyi sürekli hale getirmiştir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Geleneksel tarafta nasıl sürekli kamusal alan ön plana çıkmaktaysa modern tarafta da domestik alan baskındır. Kadın, iç mekâna hapsedilmiştir. Kadının sadece bir kere modern hayatın bir aracı olan arabayla dışarı çıkması dışında hep evde olduğu gözlemlenmektedir. Modern hayatın en önemli temsilcisi olan modern mimari, &amp;nbsp;öncelikle kadını domestik alana hapsetmiştir. Eril bir birey gibi konumlanan modern mimari, kadını hapsetmekle kalmamış aynı zamanda da köleleştirmiştir. Kadın, koca malikâneyi temizlemekten ve evin içerisindeki teknolojik araçlarla boğuşmaktan kurtulamaz. Gün boyu evin içinde dört dönen kadına boş vakit kalmaz. Oysa modern hayat ve onun getirileri, özellikle kadına daha rahat bir hayat ve boş vakit vaat etmiştir. Oysa modern çağı bizzat deneyimlemiş Avusturyalı filozof ve toplum eleştirmeni Ivan Illich, “yüz yıldır makinelerin insanlara hizmet etmesini sağlamaya, insanları da hayat boyu makineler için eğitmeye çalıştık. Artık, makinelerin ‘hizmet’ etmeyeceği ve insanların da hayat boyu makineler için eğitilemeyeceği ortaya çıktı. Bu hipodeneyin dayandığı hipotez, artık bir yana atılmalı. Bu hipotez, makinelerin, kölelerin yerini alabileceğini öngörüyordu. Kanıtlar gösteriyor ki bu amaçla kullanıldığında makineler insanları köleleştiriyor”&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;[5]&lt;/span&gt;&amp;nbsp;diyerek durumu net bir şekilde özetlemiştir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Makinelerin insanı köleleştirmesinin yanında domestik alana hapsedilen kadının aynı zamanda “ev” denilen hapishane tarafından da bir esarete mahkûm olma durumu vardır. Kadın, her çağda olduğu gibi modern çağ tarafından da eril bireyden daha çok sömürülmektedir. Eril birey her ne kadar bir başka sömürü alanına gidiyor olsa da her gün kamusal alana çıkabilmektedir. Fakat kadın…&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-large wp-image-138756 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle-1024x759.jpg&quot; alt=&quot;&quot; width=&quot;1024&quot; height=&quot;759&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle-1024x759.jpg 1024w, https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle-300x222.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle-768x569.jpg 768w, https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle-1536x1139.jpg 1536w, https://filmhafizasi.com/wp-content/uploads/2023/03/Mon_Oncle.jpg 2000w&quot; sizes=&quot;(max-width: 1024px) 100vw, 1024px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Bir Rüya Efekti: Ev&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Ev nottur. Hayatın müsveddesidir. Bir türlü tamamlanmaz.&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Başsız ve sonsuzdur. Koşarsınız ve hep aynı yerdesinizdir.&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Biraz yol aldığınızı sanırsınız, az sonra bulaşık bitecek ve mutfak silinecek ve toplanmış olacak gibi, az sonra gelir, az sonraya kadar koşmuşsunuzdur, ama az sonra o kadar az sürer ki anlamazsınız, aynı yerdesinizdir. (…)&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Ev dörtgen değildir; küp görünür, ama o da bir dairedir. İçinde dönersin. Bitmez. Ev işi dünyada sonu olmayan tek iştir. Sonucu yoktur. Efekt: Ev bir yuva efektidir&amp;nbsp;&lt;/em&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[6].&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51); text-align: right;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mavi Neşe&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Neşe’nin bahsettiği bu rüya efektinde, tam anlamıyla modern hayatın taze üretimleri boy göstermektedir. Arpel Malikânesi’nin içini bir modern sanat müzesi olarak düşünmek mümkündür. Zira işlevsellikleri olmayan fakat sanatsal değer taşıyan tüm objeler, evin gerçek hâkimi gibidir. Fakat evin en işlevsel hâkimlerinden biri televizyondur. Televizyon her akşam onu izleyen bireyleri hipnotize etmektedir. Sadece varlıkları, sesleri veya başaramadıklarıyla ön planda olan nesnelerin yanında televizyonun özel bir yeri vardır. Televizyon konuşur, gösterir, propaganda yapar ve ele geçirir. Arpel ailesinin de akşamları yaptıkları tek aktivite televizyon izlemektir. Çiftin televizyon izlediği sahnede, televizyondan gelen ışığın ele geçirici gücü görülmektedir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;“İçmekân, sanatın sığınağıdır. Koleksiyoncu ise, içmekânın gerçek sakinidir. Nesneleri yüceltmeyi görev edinmiştir. Nesneler üzerindeki mülkiyeti aracılığıyla, onları mal olma niteliklerinden arındırma gibi umutsuz bir işlevi de üstlenmiştir”&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;[7]&lt;/span&gt;. Modernizmi yanlış yorumlayan bireyin temsili olan Arpel ailesinin yetişkinleri, eve alıp yerleştirdikleri nesnelere adeta tapınmaktadırlar. Kadın, bir sergi gözcüsü gibi gün boyu onları gözünden bile sakınıp tozunu almakta ve eve gelen misafirlere sunmaktadır. Öyle ki evin içerisindeki nesneler, özne konumundaki bireyleri teslim almıştır. Ama bu esir alınma durumu en çok da kadın için geçerlidir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Tati, modern toplumun gündelik hayatında, bireyleri önemsizleştirerek nesneleri daha ön plana çıkarmış ve aktifleştirmiştir. Hareketsiz olan nesneler harekete geçerken (Mösyö Hulot’nun Arpel Malikânesi’nin mutfağındaki mücadele sahnesi), aynı zamanda mekanik sesleriyle de ön plana çıkmaktadırlar. Geleneksel tarafta kullanılan neşe dolu müzikleri duymamakla birlikte modern tarafta duyulan tek şey nesnelerin sesleri olur. Üstelik Tati, bu sesleri normalden daha ön plana çıkararak gerilimi beslemekle kalmaz aynı zamanda bireyleri konuşturmaz ya da konuştursa da anlaşılmaz, hikâyeye katkı sunmayan diyaloglarla sınırlar. Böylelikle Tati, modernizmin nesnelerini ilgi çekici, sürekli bakılan objeler olarak tanımlar, sesiyle de varlık gösteren aktif birer özneye çevirir; evin asıl sahiplerini ise özneden nesneye dönüştürür.&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle.jpg&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-large wp-image-138757 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-1024x576.jpg&quot; alt=&quot;&quot; width=&quot;1024&quot; height=&quot;576&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-1024x576.jpg 1024w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-300x169.jpg 300w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle-768x432.jpg 768w, https://filmhafizasi.com/wp-content/uploads/2023/03/mon-oncle.jpg 1200w&quot; sizes=&quot;(max-width: 1024px) 100vw, 1024px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h4 style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Bir Efendi Köle İlişkisi: Özneler ve Nesneler ya da Nesneler ve Özneler&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Modern kentin sakinleri, nesnelere sahip olduğu için bir dispozofobi&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(5)&lt;/sup&gt;&lt;/span&gt;&amp;nbsp;olmaktan öte hipnotize olmuş bireyler gibidir. Mutlak bir teslim olma ve beraberinde sahibine duyulan hayranlık gelmektedir. Yine dönemin önemli filozoflarından Walter Benjamin, “Haussmann’ın kentsel ideali, birbirini izleyen uzun caddeler boyunca perspektif ağırlıklı bir bakış sağlayabilmekti. Bu ideal, kaynağını On dokuzuncu Yüzyıl’da kendini sürekli belli eden bir eğilimde, teknik zorunlulukları sanatsal hedefler aracılığıyla soylu kılma eğiliminde bulur. Bu eğilim doğrultusunda, burjuva sınıfının dünyasal ve tinsel egemenliğinin simgesi olan kurumların, caddelerin sağlayacağı çerçeve içerisinde yüceltilmesi öngörülmüştür; caddeler, yapımları bitmezden önce çadır beziyle örtülüyor, sonra anıtlar gibi törenle açılıyordu”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[8]&amp;nbsp;&lt;/span&gt;sözlerinde dile getirdiği “eserine tapınma” durumu&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle&lt;/em&gt;’daki modern insan temsilinde karşılık bulmaktadır.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Gün boyu modern hayatın ürettiği mekânları ve objeleri bekleyen, onları temizleyerek bir nevi hizmet eden modern kent sakinleri, aynı zamanda sahibini sürekli bir başkasına sunma, ritüeli eşliğinde görücüye çıkarma alışkanlığına sahiptir. Yaratılan kentlerin dış mekânlarında sosyalleşmeyen modern kent insanı, sahip olmakla ait olmak arasında anlam karmaşasına tutulduğu mekân ve objeleri sergilemek için misafir ağırlamayı kendisine bir borç bilmektedir. Hatta Arpel Malikânesi’ndeki havuzun fıskiyesinin sadece konuklar gelince çalıştırılması da bunun en önemli kanıtı gibidir. Aslında konuklara evini bir modern sanat galerisi gibi gezdiren ve bundan büyük bir tatmin yaşayan Arpel ailesinin bireylerinin bir nevi mastürbasyon yaptığını varsaymak mümkündür. Fıskiyenin çalıştığı anlar, bu eylemin nihayete eriştiğinin en belirgin göstergesidir. Modern kent insanı, sadece göstermek için yaşamaktadır. Hayatını kendinden öte kendisine bakan üzerinden planlamıştır. Bakıldıkça ve gösterdikçe statüsünü koruyacağını düşünmektedir. Tabii modern zamanların bu handikabı günümüzde de artarak devam etmektedir. Yine Chul-Han’ın&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Şeffaflık Toplumu&lt;/em&gt;’nda dile getirdikleri, her ne kadar 21. yüzyılı ifade etse de geçmiş yüzyılı da özetler niteliktedir: “Teşhircilik toplumunda her özne kendi reklam nesnesidir. Her şey sergi değeriyle ölçülür. Teşhircilik toplumu pornografik bir toplumdur. Her şey dışa çevrilmiş, ifşa edilmiş, çıplaklaşmış, soyulmuş, ortaya serilmiş durumdadır. (…) Böylece şeylerin her türlü kendine özgülüğü feda edilmiştir”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[9]&lt;/span&gt;.&lt;a href=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/Screenshot318.png&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(247, 13, 40);&quot;&gt;&lt;img loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;size-large wp-image-138758 aligncenter&quot; src=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/Screenshot318-1024x748.png&quot; alt=&quot;&quot; width=&quot;1024&quot; height=&quot;748&quot; srcset=&quot;https://filmhafizasi.com/wp-content/uploads/2023/03/Screenshot318-1024x748.png 1024w, https://filmhafizasi.com/wp-content/uploads/2023/03/Screenshot318-300x219.png 300w, https://filmhafizasi.com/wp-content/uploads/2023/03/Screenshot318-768x561.png 768w, https://filmhafizasi.com/wp-content/uploads/2023/03/Screenshot318.png 1439w&quot; sizes=&quot;(max-width: 1024px) 100vw, 1024px&quot; data-pin-no-hover=&quot;true&quot; style=&quot;height: auto; max-width: 100%; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; clear: both; text-align: center; display: block;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h4 style=&quot;margin: 1.33em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 1.25em; line-height: 1.5; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; color: rgb(33, 33, 33); text-rendering: optimizelegibility;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Paris Sokaklarında Bir Flânör: Mösyö Hulot&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Geleneksel ile modernitenin sessiz savaşının her iki tarafında da gezinerek biz seyirciye düşünme ve tartışma alanı açan, Jacques Tati tarafından hayat bulan Mösyö Hulot; tam anlamıyla bir flâneur’dür. Flâneur, dönemin birçok düşünürünün ifade ettiği gibi “amaçsızca, rastgele yürüyerek dolaşan ve kendini çevresinin etkisine bırakıp ‘anın izini süren kişi’ olarak”&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;[10]&lt;/span&gt;&amp;nbsp;tanımlanabilir. Fakat unutulmamalıdır ki flâneur kavramı, tıpkı Fransız düşünür Walter Benjamin’in yaptığı gibi 19. yüzyılın Paris sokaklarında anlamlandırılmalıdır. Lakin flâneur, ne kent hakkında fikir sahibi olmak amacıyla kenti planlı bir şekilde adımlayan turist, ne de şehirde koşturup duran çalışan bir insandır. Aksine hiçbir amaç taşımadan şehri dolaşır. Sadece gözlemcidir. İnsanları gözlemler. Sakince sokakları, pasajları adımlar. Her zaman en işlek, en kalabalık mekânları seçer. Mösyö Hulot karakteri de Tati’nin yarattığı alternatif Paris kentinde gün boyu kenti adımlayıp durur. Ne içinde yaşayıp büyüdüğü kenti keşfetmek gibi bir niyeti ne de bir şeyleri yetiştirmek gibi bir çabası vardır. Mösyö Hulot, iş başvurusuna gittiği mekânlar da dâhil olmak üzere gittiği her yerde insanları gözlemleyip düşünür. Mösyö Hulot’un bu eleştirel bakış açısıyla, biz seyirci de seyrettiklerimizi bir flâneur gibi yorumlama şansı buluruz.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Modern kent hayatının ayrılmaz bir parçası olan flâneur, Paris sokaklarının kalabalıkları içerisinde anlam kazanır. Zira “flâneur, sığınağını kitlede arar (…) Kitle, bir peçedir; bu peçenin ardından alışılmış kent, bir fantazmagori&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(8)&lt;/sup&gt;&lt;/span&gt;&amp;nbsp;niteliğiyle flâneur’ü çağırmaktadır”&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&amp;nbsp;[11]&lt;/span&gt;. Mösyö Hulot karakteri de gün boyu kentin muhtelif yerlerinde görünür. Özellikle&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Mon Oncle&lt;/em&gt;’da geleneksel hayatın hüküm sürdüğü yerleşim birimlerinin en işlek mekânlarında varlık gösterir. Örneğin bir mahalle pazarında görünür. Lakin sık sık da modern kent yaşamının şekillenmeye başladığı tarafa geçer. Eski ile yeninin, geleneksel ile modernitenin çatıştığı bu iki bölge arasında gidip gelen Mösyö Hulot, hem geleneksel tarafı hem de modern tarafı eleştirel bir gözle aktarır. Her ne kadar yaşamını sürdürdüğü, devamlı varlık gösterdiği taraf olan geleneksel tarafa daha yakınmış gibi bir intiba çizse de günün sonunda her iki tarafa da belli bir mesafede duran bir karakterdir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Mösyö Hulot, tıpkı bir flâneur gibi kitleler arasında dolaşıp gözlem yaparken dikkat çekmemek için tebdili kıyafet gezer. Her ne kadar dönemin giyim tarzına, modaya uyum sağlayacak biri olmasa da kalabalıklar arasında dolaşırken ilgi çekmemek adına kendisi dışındaki kalabalıklarla bir örnek giyinir. Ağzında piposu, fötr şapkası, şemsiyesi, pardösüsü ile dönemin genel geçer giyim modasına uyum sağlayan bir görüntü çizer. Bu nedenle de filmlerde sık sık kitlenin içindeki diğer bireylerle karıştırılır. Bu, tam da bir flâneur’ün yapmak istediği şeydir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Mösyö Hulot’a bu gezintileri esnasında sıklıkla yeğeni Gerard da eşlik eder. Dayısını çok seven –ya da geleneksel kenti çok seven- Gerard, sıklıkla dayısının peşine takılır. El ele tutuşarak dolaşan bu ikili, Arpel ailesinin babasını oldukça sinirlendirir. Zira Mösyö Hulot gelenekseli temsil ediyorsa Arpel ailesinin “reisi” de modernizmi temsil etmektedir. Filmin sonuna kadar ebeveynlerinin aksine diğer tarafa geçerek eskiyle bağını koparmayan yani, bir anlamda yeni neslin temsilcisi Gerard,; ne yazık ki bunu çok fazla sürdüremez. Dayısının kenti terk etmesiyle birlikte çocuk, elini babasına yani modernizme uzatır. Ki zaten Tati’nin bir sonraki filmi&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Play Time&lt;/em&gt;’da kent tamamıyla cam ve çelik yığınından oluşan modern tarafa teslim olmuştur.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;Jacques Tati’nin bir önceki filmi&amp;nbsp;&lt;em style=&quot;margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Les Vacances de Monsieur Hulot’nun analizine&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;a href=&quot;https://filmhafizasi.com/bir-modern-kent-insani-elestirisi-les-vacances-de-monsieur-hulot-1953/&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);&quot;&gt;buradan&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&amp;nbsp;ulaşabilirsiniz.&lt;/em&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Dipnot:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(1)&lt;/sup&gt;&lt;/span&gt;Özellikle sessiz sinema döneminde Charlie Chaplin, Buster Keaton, Laurel ve Hardy gibi komedi sanatçılarının abartılı ve dikkatsizce yaptıkları oyunculuğa verilen isimdir. Örneğin muz kabuğuna basıp düşmek, birine vurmak isterken bir başkasına vurmak gibi…&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(2)&amp;nbsp;&lt;/sup&gt;&lt;/span&gt;1901 ile 1991 yılları arasında yaşamış Henri Lefebvre Fransız sosyolog, entelektüel ve felsefecidir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(3)&lt;/sup&gt;&lt;/span&gt;&amp;nbsp;19. Yüzyılın ortalarında III. Napolyon tarafından Seine valisi olarak atanan Haussmann, tüm Paris’i yıkıp yeniden modern bir tarz ile inşa ettirmiştir. Kendini “yıkıcı-yapıcı” olarak tanımlayan Haussmann, Paris’i kimliksizleştiren vali olarak hafızalarda yer etmiştir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(4)&lt;/sup&gt;&lt;/span&gt;1889-1976 yılları arasında yaşayan Martin Heidegger, varoluşçu felsefenin önde gelen isimlerinden biri olan Alman filozoftur.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(5)&amp;nbsp;&lt;/sup&gt;&lt;/span&gt;Peter Handke, Avusturyalı roman ve oyun yazarıdır. Ülkemizde de birçok kitabı çevrilmiştir.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(6)”&lt;/sup&gt;&lt;/span&gt;Kural Şudur: Çevrede, halka şeklinde bir bina; ortada bir kule; kulede açılmış olan geniş pencereler halkanın iç cephesine bakmaktadır. Çevre bina hücrelere ayrılmıştır, hücrelerin her biri bina boyunca derinlemesine uzanır. Bu hücrelerin iki penceresi vardır: Biri içeriye doğru açıktır, kulenin pencerelerine denk düşer; diğeri dışarıya bakarak, ışığın bir baştan bir başa hücreyi katetmesini sağlar. Bu durumda merkezi kuleye bir gözlemci yerleştirmek ve her bir hücreye bir deli, bir hasta, bir mahkûm, bir işçi ya da bir öğrenci kapatmak yeterlidir. Önden ışıklandırma sayesinde, karanlıkta kalan kuleden çevre hücrelerdeki esirlerin küçük siluetleri görülebilir”&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;[12]&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(7)&lt;/sup&gt;&lt;/span&gt;Kompulsif biriktirme hastalığı, biriktiricilik bozukluğu ya da bir diğer adıyla istifleme bozukluğu, gelecekte bir gün işe yarar düşüncesiyle, sahip olunan şeylerin değerine bakılmaksızın, sağlıksız hatta kullanılmayan tüm eşyaların, ihtiyaç gerekçesiyle elden çıkarılmaması durumudur.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;sup style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 11.25px; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; top: 0.25em;&quot;&gt;(8)&amp;nbsp;&lt;/sup&gt;&lt;/span&gt;Aldatıcı görüntü.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-alternates: inherit; font-variant-position: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: Lora, Helvetica, Arial, sans-serif; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline; text-rendering: optimizelegibility; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;Kaynakça:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 1.25em; margin-left: 0p